import React from 'react';

function Scene({ scene, onChoice }) {
  return (
    <div style={{ background: `url(/assets/backgrounds/${scene.background}) no-repeat center center`, minHeight: '60vh', color: 'white', textAlign: 'center', padding: 40 }}>
      <h2>{scene.text}</h2>
      {scene.character && (
        <div style={{ margin: '20px auto' }}>
          <img src={`/assets/sprites/${scene.character.sprite}`} alt={scene.character.name} style={{ width: 200 }} />
          <div>{scene.character.name}</div>
        </div>
      )}
      {scene.dialogue && scene.dialogue.map((d, i) => (
        <div key={i}>
          <strong>{d.speaker}:</strong> {d.text}
          <div style={{ marginTop: 20 }}>
            {d.responses && d.responses.map((resp, idx) => (
              <button key={idx} onClick={() => onChoice(resp.nextScene)} style={{ margin: 5 }}>
                {resp.text}
              </button>
            ))}
          </div>
        </div>
      ))}
      {scene.choices && scene.choices.map((choice, idx) => (
        <button key={idx} onClick={() => onChoice(choice.nextScene)} style={{ margin: 5 }}>
          {choice.text}
        </button>
      ))}
    </div>
  );
}

export default Scene;
