import React from 'react';

function HomeScreen({ onStart }) {
  return (
    <div style={{ background: '#181845', minHeight: '100vh', color: 'white', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
      <div style={{ fontSize: 48, fontWeight: 'bold', marginBottom: 32 }}>Lingo App</div>
      <div style={{ fontSize: 20, marginBottom: 32 }}>Play through interactive stories to learn new languages!</div>
      <button onClick={onStart} style={{ fontSize: 24, padding: '12px 32px', borderRadius: 8, background: '#fff', color: '#181845', fontWeight: 'bold', border: 'none', cursor: 'pointer' }}>
        Start
      </button>
    </div>
  );
}

export default HomeScreen;
